  `password` varchar(72) DEFAULT NULL COMMENT 'Contrasenya',
  `dni` varchar(12) DEFAULT NULL COMMENT 'DNI',
  `nom` varchar(45) NOT NULL COMMENT 'Nom',
  `cognoms` varchar(45) NOT NULL COMMENT 'Cognoms',
  `mail` varchar(256) DEFAULT NULL COMMENT 'E-mail',
  `naixement` varchar(12) DEFAULT NULL COMMENT 'D. naixement',
  `sexe` char(1) DEFAULT NULL COMMENT 'Sexe',
  PRIMARY KEY (`id`),
  UNIQUE KEY `identificador_UNIQUE` (`usuari`),
  UNIQUE KEY `id_UNIQUE` (`id`),
