  CONSTRAINT `fk_usuaris_tipus` FOREIGN KEY (`tipus`) REFERENCES `usuaris_tipus` (`tipus`) ON DELETE RESTRICT ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=56 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

